function searchInCondition(resType){
	$("#resType").val(resType);
	document.searchForm.submit();
}

function judgeLeft(width,left,orderTime,imgwidth){
	if( width-left < orderTime){
		left = left-721;
	}
	if(left < orderTime　){
		left = left+imgwidth;
	} 
	return left;
}

function collectInstrument(insUid){
    var myData={
        "insUid":insUid,
    }
    $.ajax({
        url:"../instrument/collectInstrument",
        type:'POST',
        async:false,
        data:myData,
        success:function(data){//AJAX查询成功
            if(data=="success"){
                $("#clollected"+insUid).removeClass("hide");
                $("#unclollected"+insUid).addClass("hide");
            }else{
                alert("收藏失败！");
            }
        }
    })
}
function uncollectInstrument(insUid){
    var myData={
        "insUid":insUid,
    }
    $.ajax({
        url:"../instrument/uncollectInstrument",
        type:'POST',
        async:false,
        data:myData,
        success:function(data){//AJAX查询成功
            if(data=="success"){
                $("#unclollected"+insUid).removeClass("hide");
                $("#clollected"+insUid).addClass("hide");
            }else{
                alert("取消收藏失败！");
            }
        }
    })
}

