function selecthighlight(){
    var flag=$("#selectsaaa").attr("data");
    if(flag==4){
        $($("#selectsaaa").children().get(flag)).attr("class","order_select");
    }
    if(flag==0){
        $($("#selectsaaa").children().get(flag)).attr("class","order_select");
    }
    if(flag==1){
        $($("#selectsaaa").children().get(flag)).attr("class","order_select");
    }
    if(flag==2){
        $($("#selectsaaa").children().get(flag)).attr("class","order_select");
    }
    if(flag==3){
        $($("#selectsaaa").children().get(flag)).attr("class","order_select");
    }
}
